import React, {useState, useEffect} from "react";
import Node from  "./Node"
import "./Pathfind.css"
import Astar from "../astarAlgorithm/astar.js"

const cols = 12;
const rows = 10;


const NODE_START_ROW = rows-1;
const NODE_START_COL = cols-2;
const NODE_END_ROW = 1;
const NODE_END_COL = 1;
let Goal_Nodes_ROW = [1,4]
let Goal_Nodes_COL = [1,8]

const Pathfind = () =>{

    const [Grid, setGrid] = useState([]);
    const [Path, setPath] = useState([]);

    useEffect(() => {
        initializeGrid();
    });

    const initializeGrid = () =>{
        const grid = new Array(rows);
        for(let i = 0; i < rows; i++){
            grid[i] = new Array(cols);
        }
        createSpot(grid);
        setGrid(grid);
        addNeighbors(grid);

        let minIndex = 0;
        let minRow = Goal_Nodes_ROW[0];
        let minCol = Goal_Nodes_Col[0];
        let minDistance = Math.abs(minRow-NODE_START_ROW) + Math.abs(minCol-NODE_START_COL);
        while(Goal_Nodes_ROW.length > 0){
            let minRow = Goal_Nodes_ROW[0];
            let minCol = Goal_Nodes_Col[0];
            for(let i =0; i<Goal_Nodes_ROW.length; i++){
                let y = Goal_Nodes_ROW[i];
                let x = Goal_Nodes_COL[i];
                let distance = Math.abs(y-NODE_START_ROW) + Math.abs(x-NODE_START_COL);
                if(distance < minDistance){
                    minIndex = i;
                }
            }
            let goalColumn = Goal_Nodes_COL.splice(minIndex, 1)[0]
            let goalRow = Goal_Nodes_ROW.splice(minIndex, 1)[0];
            console.log(goalColumn + " " + goalRow);
        }

        const startNode = grid[NODE_START_ROW][NODE_START_COL];
        const endNode = grid[NODE_END_ROW][NODE_END_COL];
        let path = Astar(startNode, endNode);
        startNode.isWall = false;
        endNode.isWall = false;
        setPath(path);
        Astar(startNode, endNode);
    };

    const createSpot = (grid) =>{
        for(let i=0; i< rows; i++){
            for(let j=0; j < cols; j++){
                grid[i][j] = new Spot(i, j)
            }
        }
    }

    const gridwithNode = (
        <div>
            {Grid.map((row, rowIndex) => {
                return (
                    <div key={rowIndex} className="rowWrapper">
                        {row.map((col, colIndex) => {
                            const {isStart, isEnd, isWall} = col;
                            return(
                                <Node key={colIndex} isStart={isStart} isEnd={isEnd} row={rowIndex} col={colIndex} isWall={isWall}/>
                            )
                        })}
                    </div>
                )
            })}
        </div>
    );


    const visualizePath = () => {
        for(let i=0; i<Path.length; i++){
            const node = Path[i];
            document.getElementById(`node-${node.x}-${node.y}`).className = 'node node-shortest-path'
        }
        console.log("Visualizing")
    }

    console.log(Path);
    return(
        <div className="Wrapper">
        <button onClick={visualizePath}>Visualize Path</button>
            <h1>Pathfind component</h1>
            {gridwithNode}
        </div>
        );  
};

const addNeighbors = (grid) => {
    for(let i=0; i< rows; i++){
        for (let j=0; j< cols; j++){
            grid[i][j].addneighbors(grid);
        }
    }
}

function getAisles(x, y){
    let isWall = false;
    if (y == 0 || y== cols-1 ){
        isWall = true;
    }
    if(x == 0 || x == rows -1){
        isWall = true;
    }
    if(x == 2 || x ==3 || x == 5 || x == 6){
        if( y >=2 && y<=4){
            isWall = true;
        }
        if(y >= 6 && y <=9 ){
            isWall = true;
        }
    }
    return isWall;
}

function Spot(i, j){
    this.x = i;
    this.y = j;
    this.isStart = this.x === NODE_START_ROW  && this.y === NODE_START_COL;
    this.isEnd = this.x === NODE_END_ROW && this.y === NODE_END_COL;
    this.g = 0;
    this.f = 0;
    this.h = 0;
    this.neighbors = [];
    this.isWall = getAisles(i, j);
    // this.isWall = false;
    // if(Math.random(1) < 0.2){
    //     this.isWall = true;
    // }
    this.previous = undefined;
    this.addneighbors = function(grid){
        let i = this.x;
        let j = this.y;
        if(i > 0) this.neighbors.push(grid[i-1][j])
        if(i < rows - 1) this.neighbors.push(grid[i+1][j])
        if(j > 0) this.neighbors.push(grid[i][j-1]);
        if(j < cols - 1) this.neighbors.push(grid[i][j+1]);
    };
}

export default Pathfind;
